import { style } from '@angular/animations';
import { Component } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html'
})
export class HeaderComponent {
  title:String = "Angular App"
  foco:string;
  empleados:any = document.getElementById('cli');

  obtenerFoco(event:Event){
    this.foco = (<HTMLInputElement>event.target).value;
    if(this.foco == 'cli'){
      this.empleados.fontColor("red");
    }
  }
}
