import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import Swal from 'sweetalert2';
import { Cliente } from './cliente';
import { ClienteService } from './cliente.service';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html'  
})
export class FormComponent {
  public titulo:string = 'Crear Cliente';
  public cliente:Cliente = new Cliente();

  constructor(private clienteService:ClienteService, private router:Router, private activatedRoute:ActivatedRoute){
    this.cargarCliente();
  }

// cargar el cliente segun su id 
cargarCliente(): void{
  this.activatedRoute.params.subscribe(params =>{
    let id = params['id'];
    if(id){
      this.clienteService.getCliente(id).subscribe((cliente) => this.cliente = cliente);
    }
  });
}

  // crea el cliente, guarda y retorna a /clientes
  public create(): void{
    this.clienteService.create(this.cliente).subscribe(cliente => {
      this.router.navigate(['/clientes'])
      Swal.fire('Cliente guardado',`Cliente ${cliente.nombre} guardado con éxito`, 'success');
    });
  }

}
<style>  
    .title{
        text-align: center;
        margin-top: 10px;
        font-size: 24px;
        font-weight: bold;
    }    
</style>
<br><br><br>
<div class="container card bg-dark text-white">
    <div class="card-header title">{{titulo}}</div>
    <div class="card-body">
        <form (ngSubmit)="create()" ngNativeValidate>
            <div class="form-group row">
                <label for="" class="col-form-label col-sm-2">Nombre</label>
                <div class="col-sm-6">
                    <input type="text" required class="form-control" name="nombre" [(ngModel)]="cliente.nombre">
                </div>
            </div><br>
            <div class="form-group row">
                <label for="" class="col-form-label col-sm-2">Apellido</label>
                <div class="col-sm-6">
                    <input type="text" required="" class="form-control" name="apellido" [(ngModel)]="cliente.apellido">
                </div>
            </div><br>
            <div class="form-group row">
                <label for="" class="col-form-label col-sm-2">Email</label>
                <div class="col-sm-6">
                    <input type="email" required class="form-control" name="email" [(ngModel)]="cliente.email">
                </div>
            </div><br>
            <div class="form-group row" >
                <div class="col-sm-6">
                    <button class="btn btn-primary" role="button">Crear</button>
                </div>
            </div>
        </form>
    </div>
</div>
